-- Table structure for `tushuxujie`
-- ----------------------------
DROP TABLE IF EXISTS `tushuxujie`;
CREATE TABLE `tushuxujie` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `tushujieyueid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '图书借阅id',
  `tushuhao` varchar(50) NOT NULL DEFAULT '' COMMENT '图书号',
  `tushumingcheng` varchar(255) NOT NULL DEFAULT '' COMMENT '图书名称',
  `tushuleibie` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '图书类别',
  `jieyuedanhao` varchar(50) NOT NULL DEFAULT '' COMMENT '借阅单号',
  `jieyueyonghu` varchar(50) NOT NULL DEFAULT '' COMMENT '借阅用户',
  `jieyueshijian` varchar(25) NOT NULL DEFAULT '' COMMENT '借阅时间',
  `guihuanshijian` varchar(25) NOT NULL DEFAULT '' COMMENT '归还时间',
  `xujiedao` varchar(25) NOT NULL DEFAULT '' COMMENT '续借到',
  `xujiebeizhu` text NOT NULL COMMENT '续借备注',
  PRIMARY KEY (`id`),
  KEY `tushujieyue_index` (`tushujieyueid`),
  KEY `tushuleibie_index` (`tushuleibie`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8mb4 COMMENT='图书续借';

